import React, {useEffect,useState, Component } from 'react';
import firebase from '../Componentes/Firebase';

class PerfilPage extends Component{

    datos;
    constructor(props){
        super(props);
        this.state = {
            error : null,
            isLoaded : false,
            datos :[]
        }
    }

   
    componentDidMount(){
           firebase.database().ref('usuarios/'+this.props.match.params.id).once('value').then(snapshot =>{
               console.log("snapshot",snapshot.val());
               this.setState({
                datos: snapshot.val(),
                isLoaded:true
               } )
           })
        
        
    }
    render(){

        const { error, isLoaded, datos} = this.state;
        if(error){
            return <div>Error: {error.message}</div>;
        }else if(!isLoaded){
            return <div> Loading...</div>;
        }else{
            return (
              <div>
                    {Object.keys(this.state.perfiles).map((k,v)=>
                    <ListaPerfiles datos={this.state.perfiles[k]}/>)}
              </div>
                  
               
                    
            
            );
        }
      
    }
}

export default PerfilPage;
