import React, { Component } from 'react';
// import './../Componentes/Estilo.css';
import firebase from '../Componentes/Firebase';
import ListaPerfiles from '../Componentes/ListaPerfiles';
class HomePage extends Component{
    perfiles;
    constructor(props){
        super(props);
        this.state = {
            error : null,
            isLoaded : false,
            perfiles :[]
        }
    }

componentDidMount(){
    var this_class = this;
    return firebase.database().ref('usuarios/').once('value').then(function(snapshot){
        this_class.setState({
            nombre: snapshot.val().nombre
        })
    })
}
  

    componentWillMount(){
        fetch("https://jsonplaceholder.typicode.com/users")
        .then(res => res.json())
        .then(
            (result) => {
                console.log(result)
                this.setState({
                    isLoaded : true,
                    perfiles : result
                });
            },
            (error) => {
                console.log(error)
                this.setState({
                    isLoaded : true,
                    error
                });
            }
        )
  
    }
    render(){
        const { error, isLoaded, perfiles} = this.state;
        if(error){
            return <div>Error: {error.message}</div>;
        }else if(!isLoaded){
            return <div> Loading...</div>;
        }else{
            return (
                <ul>
                    {perfiles.map(
                        perfil => <ListaPerfiles datos={perfil}/>
                    )}
                </ul>
            );
        }
   
    }
}
